    private static void findSubsubCat(String url) {
        try {

            Document doc = Jsoup.connect(url)
                    .ignoreContentType(true)
                    .ignoreHttpErrors(true)
                    .timeout(0).get();
            boolean subcat = false;
            boolean subsubcat = false;
            Element catDiv = doc.getElementById("search-filter-reset-form");
            if (!catDiv.getElementsByClass("list-unstyled").isEmpty()) {
                Elements ulTags = catDiv.getElementsByClass("list-unstyled");
                for (Element ul : ulTags) {
                    Elements liTags = ul.getElementsByTag("li");
                    for (Element li : liTags) {
                        if (!li.getElementsByTag("span").isEmpty() && !li.getElementsByTag("span").first().classNames().contains("ss-icon")) {
                            if (li.getElementsByTag("a").first().classNames().contains("strong")) {
                                subcat = true;
                                //findLastOne(detailUrl, li.getElementsByTag("a").first().text());
                            }
                            if (subcat) {
                                String detailUrl = li.getElementsByTag("a").first().attr("href");
                                System.out.println(li.getElementsByTag("a").first().text() + "| " + detailUrl);
                                //  subsubcat=true;
                                // findSubsubCat(detailUrl);
                            }
                        }
                    }
                }
            } else {
                System.out.println("Sub cat not found");
            }
            System.out.println("");

        } catch (IOException ex) {
            Logger.getLogger(GetCategory.class
                    .getName()).log(Level.SEVERE, null, ex);
        }
    }

